# Part A
print("Day 6: Part A = ", end='')

line = ''

def check_marker(marker_ls):
    for ch in marker_ls:
        if marker_ls.count(ch) > 1:
            return False
    return True

count = 0
marker = []
for ch in line:
    count += 1
    marker.append(ch)

    if len(marker) > 3:
        if check_marker(marker):
            break
        else:
            marker.pop(0)

print(count)

# Part B
print("Day 6: Part B = ", end='')

count = 14
marker = []
for i in range(14, len(line)):
    count += 1
    marker.append(line[i])

    if len(marker) > 13:
        if check_marker(marker):
            break
        else:
            marker.pop(0)

print(count)

